    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       
      
                 s_noo.setText("");    
             String emaill = user_name_txt.getText();
        String passs = new String(pass_pass_home.getPassword());
        try{Class.forName("com.mysql.jdbc.Driver");
        con = DriverManager.getConnection("jdbc:mysql://localhost/ip_pro","root","tiger");
        stmt= con.createStatement();
        rs = stmt.executeQuery("select * from user_data where email='"+ emaill +"' ;");
        while(rs.next()){
        String pwd = rs.getString("password");
        if(pwd.equals(passs)){main_page.setVisible(true);  
       int s_nooo=rs.getInt("s_no");
       s_noo.setText(""+s_nooo);
        JOptionPane.showMessageDialog(null,"You Have Signed In Successfully"); }
        
        else {JOptionPane.showConfirmDialog(null,"Username Or Password Is Incorrect!",null,-1,0);}
        }
     rs.close();
     stmt.close();
     con.close();
        }
        catch(Exception e)
        {JOptionPane.showMessageDialog(null,e.getMessage());}
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

